actions.script:
const vehicle = runtime.objects.vehicle.getPickedInstances()[0];

const check = checkLock(vehicle, runtime.objects.lockCheck.getPickedInstances()[0]);
// console.log(check);

if(check) {
	if(!lockIntersection(vehicle, runtime.objects.intersection.getAllInstances())) {
		vehicle.instVars.waiting = true;
	}
}
